class Solution:
    def findAnagrams(self, s: str, p: str):
        len_of_s = len(s)
        len_of_p = len(p)
        res = []
        if len_of_p > len_of_s: # 如果s串小于p串的话，直接返回空就好了，因为根本就不存
            return res
        target = ''.join(sorted(p)) # 接下来就判断是不是跟他相等就好了，
        for i in range(0,len_of_s-len_of_p+1):# 因为需要比对最后一个
            if ''.join(sorted(s[i:i+len_of_p])) == target: # 虽然这么做很容易，但是时间复杂度会有点高，因为每一次都需要进行排序，不进行排序也是可以的，就需要统计每个字母的频度，稍稍有点麻烦而已
                res.append(i)            
        return res
    
    
    
s = ""
p = "yqrbgjdwtcaxzsnifvhmou"
solution = Solution()
print(solution.findAnagrams(s,p))